def f(x, y):
    return ((y // 17) // (1 << (17 * x + (y % 17)))) % 2

def fk(k: int) -> list:
    lists = [[] for x in range(17)]
    for y in range(16, -1, -1):
        for x in range(105, -1, -1):
            lists[y].append(int(f(x, y + k) > 1 / 2))
    return lists

bl = fk()
for i in range(16, -1, -1):
    s = ''
    for j in range(105, -1, -1):
        if bl[i][j] == 1:
            s += '▒'
        else:
            s += '█'
    print(s)